import React from 'react';
import PropTypes from 'prop-types';
import { useTheme } from '../../utils/theme';
import styleModules from './Button.module.css';
const sizes = {
  sm: {
    paddingSize: 0.5,
    fontSize: 0.75,
  },
  md: {
    paddingSize: 0.75,
    fontSize: 1,
  },
  lg: {
    paddingSize: 1,
    fontSize: 1.2,
  },
};
const Button = ({
  label,
  size,
  onClick,
  disabled,
  type,
  rounded,
  style = {},
}) => {
  const theme = useTheme();
  const styles = {
    ...style,
    '--primaryColor': theme.primaryColor,
    '--secondaryColor': theme.secondaryColor,
    '--paddingSize': sizes[size ?? 'md'].paddingSize,
    '--fontSize': sizes[size ?? 'md'].fontSize,
    '--shadowColor': theme.shadowColor ?? '#1c1d1d',
  };

  return (
    <button
      className={`${styleModules.button} ${styleModules[type ?? 'primary']} ${
        rounded ? styleModules.rounded : ''
      }`}
      style={styles}
      onClick={onClick}
      disabled={disabled}
    >
      {label}
    </button>
  );
};

Button.propTypes = {
  label: PropTypes.string,
  size: PropTypes.oneOf(['sm', 'md', 'lg']),
  onClick: PropTypes.func,
  disabled: PropTypes.bool,
  type: PropTypes.oneOf(['primary', 'secondary']),
  rounded: PropTypes.bool,
  style: PropTypes.object,
};

export default Button;
